import { Col, Button, Row, Space, Typography, Modal, Image, Divider } from 'antd'
import React, { useEffect, useState } from 'react'
import Team from './Team'
import { MdOutlineConstruction } from 'react-icons/md'
import { useParams } from 'react-router-dom'
import axios from 'axios';
import moment from 'moment'
import { Spin } from 'antd';
import { LoadingOutlined } from '@ant-design/icons';

import { useNavigate } from 'react-router-dom'
import { FaChartLine } from 'react-icons/fa'
import { getTotalBuilder, getTotalWorker } from '../../own-comp'

export default function ModalDetail({ open, setOpen, id }) {
    const [report, setReport] = useState({})
    const [loading, setLoading] = useState(true);
    const [success, setSuccess] = React.useState(false);

    const antIcon = <LoadingOutlined style={{ fontSize: 24 }} spin />;

    useEffect(() => {
        setLoading(true)
        getConstructById(id);
    }, [id])

    const getConstructById = async (id) => {
        const params = new URLSearchParams();
        params.append('db_user', process.env.React_App_DB_USER);
        params.append('db_password', process.env.React_App_DB_PASSWORD);
        params.append('db', process.env.React_App_DB);
        params.append('data', JSON.stringify({ dc_id: id }));

        return await axios.post(
            `${process.env.React_App_URL}/get/getDailyConstructById.php`, params
        )
            .then(async function (response) {
                if (await response?.data !== 'Cannot select' && await response?.data !== 'notuser') {
                    setReport(response?.data)
                    setLoading(false)
                    setSuccess(false);
                    return response?.data;
                } else {
                    setLoading(false)
                    return [];
                }

            });
    }

    return (
        <Modal
            title={null}
            visible={open}
            onCancel={() => setOpen(false)}
            footer={null}
        >
            <Row
                style={{
                    paddingTop: "30px"
                }}
            >
                {
                    loading ? <Col xs={24} sm={24} md={24} lg={24} xl={24}>
                        <div className="loading-data"><Spin indicator={antIcon} /></div>
                    </Col>
                        :
                        <>
                            <Col
                                xs={24} sm={24} md={24} lg={24} xl={24}
                                style={{
                                    background: "#ffd359",
                                    height: "45px",
                                    textAlign: "center",
                                    padding: "15px",
                                    fontWeight: "bold",
                                    fontSize: "11pt"
                                }}
                            >
                                {`${report?.constructionName} - `}<Typography.Text type='danger'>{moment(report?.createdAt).format("DD/MM/YYYY")}</Typography.Text>
                            </Col>

                            <Col
                                xs={24} sm={24} md={24} lg={24} xl={24}
                                style={{
                                    padding: "5px"
                                }}

                            >
                                <p
                                    style={{
                                        marginTop: "13px",
                                        fontWeight: "bold",
                                        fontSize: "18px",
                                        fontFamily: "Bayon",
                                        color: '#616161',
                                    }}
                                >
                                    <MdOutlineConstruction /> មេកា៖ {`${report?.chiefName}`} <span style={{ fontFamily: 'Siemreap', fontWeight: 'bold' }}>({report?.team?.length} ក្រុម - {getTotalBuilder(report?.team) + getTotalWorker(report?.team)} នាក់)</span>
                                </p>
                                {
                                    report?.team?.map(team => <Team key={team.dt_id} team={team} />)
                                }

                                <p
                                    style={{
                                        fontSize: "16px",
                                        fontFamily: "Bayon",
                                        // color: 'red',
                                        marginTop: "5px",
                                        marginLeft: "3px"
                                    }}
                                >
                                    ជាង៖ {getTotalBuilder(report?.team) - parseInt(report?.painterCount)} | ជាងថ្នាំ៖ {report?.painterCount} | ក.ប្រុស៖ {getTotalWorker(report?.team) - parseInt(report?.femaleWorkerCount)} | ក.ស្រី៖ {report?.femaleWorkerCount}
                                </p>

                                <Divider />

                                <p
                                    style={{
                                        fontSize: "16px",
                                        fontFamily: "Bayon",
                                        color: 'red',
                                        marginTop: "10px",
                                        marginBottom: "15px",
                                        marginLeft: "3px"
                                    }}
                                >
                                    បញ្ហា៖ {report?.challenges}
                                </p>

                                <p
                                    style={{
                                        marginTop: "13px",
                                        fontWeight: "bold",
                                        fontSize: "18px",
                                        fontFamily: "Bayon",
                                        color: '#616161',
                                    }}
                                >
                                    <FaChartLine /> ស្ថិតិកម្លាំងសម្រាប់ថ្ងៃស្អែក៖

                                </p>

                                <Row
                                    style={{
                                        fontWeight: "bold",
                                        fontSize: "15px",
                                        marginBottom: "20px"
                                    }}
                                >
                                    <Col xs={12} style={{ padding: 5 }}>
                                        ជាងសំណង់៖ {report?.builderCountTmr}
                                    </Col>
                                    <Col xs={12} style={{ padding: 5 }}>
                                        ជាងថ្នាំ៖ {report?.painterCountTmr}
                                    </Col>
                                    <Col xs={12} style={{ padding: 5 }}>
                                        កម្មករប្រុស៖ {report?.workerCountTmr}
                                    </Col>
                                    <Col xs={12} style={{ padding: 5 }}>
                                        កម្មករស្រី៖ {report?.femaleWorkerCountTmr}
                                    </Col>
                                </Row>

                                <Row
                                    style={{
                                        fontWeight: "bold",
                                        fontSize: "15px",
                                        marginBottom: "10px"
                                    }}
                                >
                                    <Col xs={24} md={12} style={{ padding: 5 }}>
                                        {
                                            report?.startImage !== '' ?
                                                <>
                                                    <Image
                                                        style={{
                                                            objectFit: "cover"
                                                        }}
                                                        width={200}
                                                        height={200}
                                                        src={`${process.env.React_App_IMAGES}/${report?.startImage}`}
                                                    />
                                                </>
                                                :
                                                <>
                                                    <Image
                                                        style={{
                                                            objectFit: "cover"
                                                        }}
                                                        width={200}
                                                        height={200}
                                                        src={"error"}
                                                        fallback="data:image/png;base64,"
                                                    />
                                                </>
                                        }


                                    </Col>
                                    <Col xs={24} md={12} style={{ padding: 5 }}>
                                        {
                                            report?.resultImage !== '' ?
                                                <>
                                                    <Image
                                                        style={{
                                                            objectFit: "cover"
                                                        }}
                                                        width={200}
                                                        height={200}
                                                        src={`${process.env.React_App_IMAGES}/${report?.resultImage}`}
                                                    />

                                                </>
                                                :
                                                <>
                                                    <Image
                                                        style={{
                                                            objectFit: "cover"
                                                        }}
                                                        width={200}
                                                        height={200}
                                                        src={"error"}
                                                        fallback="data:image/png;base64,"
                                                    />
                                                </>
                                        }
                                    </Col>

                                </Row>

                            </Col>
                        </>

                }

            </Row>
        </Modal>
    )
}
